CSV2RDF4LOD_HOME=${CSV2RDF4LOD_HOME:?"not set; source csv2rdf4lod/source-me.sh or see https://github.com/timrdf/csv2rdf4lod-automation/wiki/CSV2RDF4LOD-not-set"}

delete="false"
if [ ! -e publish/cybershare-utep-edu-GravityMapPML-2011-Jul-25.void.nt ]; then
   # Note: tarball does not need to be handled b/c only layer dump files are tarballed.
   echo "cHuNking publish/cybershare-utep-edu-GravityMapPML-2011-Jul-25.void.ttl into publish/cybershare-utep-edu-GravityMapPML-2011-Jul-25.void.nt; will delete when done lod-mat'ing"
